import streamlit as st
import random
import string

# Set Page Config
st.set_page_config(page_title="Password Generator", page_icon="üîë", layout="centered")

# Custom CSS to Set Background Image
background_url = "data:image/jpeg;base64,"  # Change URL for a different image
st.markdown(
    f"""
    <style>
    .stApp {{
        background: url("{background_url}") no-repeat center center fixed;
        background-size: cover;
    }}
    .password-box {{
        background-color: rgba(0, 0, 0, 0.7);
        padding: 10px;
        border-radius: 10px;
        text-align: center;
        font-size: 1.5rem;
        color: #00ffcc;
        font-weight: bold;
        margin-top: 20px;
    }}
    h1, label {{
        color: white;
        text-align: center;
    }}
    </style>
    """,
    unsafe_allow_html=True
)

# UI Title
st.title("üîê Password Generator")

# Function to Generate Password
def generate_password(length, use_digit, use_special):
    characters = string.ascii_letters  # Uppercase and lowercase letters
    if use_digit:
        characters += string.digits  # Numbers 0 to 9
    if use_special:
        characters += string.punctuation  # Special characters
    return "".join(random.choice(characters) for _ in range(length))

# UI Components
length = st.slider("üî¢ Password Length", min_value=6, max_value=32, value=12)
use_digit = st.checkbox("üî¢ Include Digits")
use_special = st.checkbox("üî£ Include Special Characters")

# Generate Button
if st.button("üé≤ Generate Password"):
    password1 = generate_password(length, use_digit, use_special)
    st.markdown(f'<div class="password-box">üîë {password1}</div>', unsafe_allow_html=True)
